import '../App.css';
import React, { Component } from 'react';
import { useOnDraw } from './hooks';

const Canvas = () => {

    const setCanvasRef = useOnDraw(onDraw)

    function onDraw(ctx, point){

        ctx.fillStyle = '#000000';
        ctx.beginPath();
        // console.log('point2:' +point.x+ ', '+ point.y)
        ctx.arc(point.x, point.y, 2, 0, 2*Math.PI); //draw cirlce?
        ctx.fill();

    }

    return (
        <div className='CanvasWrap'>
            {/* <h1>This is where the canvas will be</h1> */}
            <canvas id='Canvas' ref={setCanvasRef}> 
            </canvas>
        </div>

    );

}

export default Canvas;
