import React, { Component } from 'react';
import '../App.css';
import { SidebarData, ColourData, ReferencesData } from './sidebar_data'

import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';


class SideBar extends Component {
    state = { clicked: -1 } 

    toggle = index => {
        if (this.state.clicked == index) {
            this.setState({ clicked: -1 });
        }
        else{
            this.setState({ clicked: index });
        }
    };

    // toggle = (link) =>{
    //     if (window.location.pathname == link){
    //         window.location.pathname = ''
    //     }
    //     else{
    //         window.location.pathname = link
    //     }
    //     console.log(window.location)
    // };

    render() { 
        console.log('clicked'+this.state.clicked)
        return (
            <div className='Sidebar'>
                <ul className='SidebarList'>
                    {SidebarData.map((val, key) => {
                        console.log('val'+val+ ' key'+key)
                        return(
                        <li key={key} 
                            className='row' 
                            onClick={() => {window.location.pathname=val.link;}}
                            id = {window.location.pathname == val.link ? "active" : "inactive"}
                        >
                            <div id='icon'>{val.icon}</div>
                            <div id='title'>{val.title}</div>
                            <div id='ExpandIcon'
                                onClick={this.toggle(key)} 
                            >
                                {/* {window.location.pathname == val.link ? <ExpandMoreIcon /> : <ExpandLessIcon />} */}
                                {this.state.clicked == key ? <ExpandMoreIcon /> : <ExpandLessIcon />}
                            </div>

                            <div className='Content'>
                                <p>hello</p>
                            </div>

                        </li>)})
                    }
                </ul>
            </div>
        )
    }
}

export default SideBar;
