import React, { Component } from 'react';
import '../App.css';
import { SidebarData, ColourData, ReferencesData } from './sidebar_data'

import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';


class SideBar extends Component {
    state = { clicked: 0 } 

    toggle = index => {
        console.log('index: '+index)
        // const clicked = this.state.clicked;
        // if (clicked === index){
        //     const new_clicked = null
        // }
        // const new_clicked = index
        // this.setState({new_clicked});
    };

    render() { 
        console.log('clicked'+this.state.clicked)
        return (
            <div className='Sidebar'>
                <ul className='SidebarList'>
                    {SidebarData.map((val, key) => {
                        return(
                        <li key={key} 
                            className='row' 
                            // onClick={() => {window.location.pathname=val.link;}}
                            onClick={() => this.toggle(val.index)} 
                            id = {window.location.pathname == val.link ? "active" : "inactive"}
                        >
                            <div id='icon'>{val.icon}</div>
                            <div id='title'>{val.title}</div>
                            <div id='ExpandIcon'>
                                {/* {window.location.pathname == val.link ? <ExpandMoreIcon /> : <ExpandLessIcon />} */}
                                {this.state.clicked === key ? <ExpandMoreIcon /> : <ExpandLessIcon />}
                            </div>

                            <div className='Content'>
                                <p>hello</p>
                            </div>

                        </li>)})
                    }
                </ul>
            </div>
        )
    }
}

export default SideBar;
