import React, { Component } from 'react';
import '../App.css';
import { SidebarData, ColourData, ReferencesData } from './sidebar_data'

import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';


class SideBar extends Component {
    state = { clicked: false } 

    // toggle = index => {
    //     if (this.state.clicked === index) {
    //         this.setState({ clicked: false });
    //     }
    // };

    toggle = (link) =>{
        return window.location.pathname == link ? <ExpandLessIcon /> : <ExpandMoreIcon />
    };

    render() { 
        return (
            <div className='Sidebar'>
                <ul className='SidebarList'>
                    {SidebarData.map((val, key) => {return(
                        <li key={key} 
                            className='row' 
                            onClick={() => {window.location.pathname=val.link;}}
                            id = {window.location.pathname == val.link ? "active" : "inactive"}
                        >
                            <div id='icon'>{val.icon}</div>
                            <div id='title'>{val.title}</div>
                            <div id='ExpandIcon'>{this.toggle(val.link)}</div>

                            <div className='Content'>
                                <p>hello</p>
                            </div>

                        </li>)})
                    }
                </ul>
            </div>
        )
    }
}

export default SideBar;
